# ----- Full layout -----
layout = [
    [
        sg.Column(xilos),
    ]
]
# Create the window
window = sg.Window("Demo", layout)

# Create an event loop
graphic_off = [True for i in range(0, 4)]
port = mido.open_output()
while True:
    event, values = window.read()
    print(event, values)

    if "TOGGLE-" in event:
        print(graphic_off)
        index = int(str(event).split('-')[1])
        graphic_off[index] = not graphic_off[index]
        window[event].update(
            image_data=toggle_btn_off if graphic_off[index] else toggle_btn_on
        )
    if "TRAIN" in event:
        index = str(event).split('-')[1]
        if values[f"CHANNEL-{index}"] == '' or values[f"CC-{index}"] == '':
            sg.popup_error("Select MIDI Channel and CC values to train")
        else:
            count = 5
            while count > 0:
                window[f"COUNTDOWN-{index}"].update(str(count))
                window.refresh()
                count -= 1
                time.sleep(1)
            window[f"COUNTDOWN-{index}"].update("")
            window.refresh()
            # send midi CC
            msg = Message(
                'control_change',
                channel=int(values[f"CHANNEL-{index}"]) - 1,
                control=values[f"CC-{index}"],
                value=127
            )
            port.send(msg)
    if event == "START":
        try:
            settings.init(values, graphic_off)
            xilo_handler = XilophoneHandler(
                values["IMAGE"],
                2,
            )
            xilo_handler_thread = threading.Thread(
                target=xilo_handler.xilo_lifecycle, daemon=True
            )
            xilo_handler_thread.start()
            # start video
            video_tracker(4)
            settings.keep_playing = False
        except(KeyboardInterrupt):
            settings.keep_playing = False
            break

    if event == sg.WIN_CLOSED:
        break

window.close()
